import { Footer } from "../../components/footer";
import { Header } from "../../components/header";
import { Navigation } from "../../components/navigation";
import { Product } from "../../components/product/product";

import axios from "axios";
import { useEffect, useState } from "react";

export const Products = () => {
  const [products, setProducts] = useState();
  // Fetch API to get data from mysql
  useEffect(() => {
    const retrieveProducts = async () => {
      const retrieveProductsAPI = await axios
        .get("http://localhost:5000/products")
        .then((d) => d.data);
      setProducts(retrieveProductsAPI);
    };
    retrieveProducts();
  }, []);

  return (
    <>
      <Navigation />
      <main>
        <Header />
        <section className="deals-banner">
          <a>
            <h1 className="deals-title">Newly added gear from $9.99</h1>
            <p className="deals-description">Sale going from 9/12 - 10/2</p>
          </a>
        </section>
        <div>
          {products && products.map((e) => <Product productData={e} />)}
        </div>
        <Footer />
      </main>
    </>
  );
};
