import React, { useEffect, useRef } from "react";

export const Header = () => {
  const menuRef = useRef(null);
  const navIconRef = useRef(null);
  const invisRef = useRef(null);

  useEffect(() => {
    const handleMenuClick = () => {
      const marginProp = window
        .getComputedStyle(menuRef.current)
        .getPropertyValue("margin-left");

      if (marginProp === "-300px") {
        menuRef.current.style.marginLeft = "0px";
        navIconRef.current.style.position = "fixed";
        invisRef.current.style.position = "relative";
      } else if (marginProp === "0px") {
        menuRef.current.style.marginLeft = "-300px";
        navIconRef.current.style.removeProperty("position");
        invisRef.current.style.position = "absolute";
      }
    };

    const handleMainClick = () => {
      const marginProp = window
        .getComputedStyle(menuRef.current)
        .getPropertyValue("margin-left");

      if (marginProp === "0px") {
        menuRef.current.style.marginLeft = "-300px";
        navIconRef.current.style.removeProperty("position");
        invisRef.current.style.position = "absolute";
      }
    };

    navIconRef.current.addEventListener("click", handleMenuClick);
    document.querySelector("main").addEventListener("click", handleMainClick);

    return () => {
      navIconRef.current.removeEventListener("click", handleMenuClick);
      document
        .querySelector("main")
        .removeEventListener("click", handleMainClick);
    };
  }, []);

  return (
    <>
      <header className="header-global">
        <nav className="menu">
          <section className="invisible" ref={invisRef}></section>
          <button className="nav-btn" ref={navIconRef}>
            <img
              className="nav-icon"
              src={require("../images/Icons/menu-icon.png")}
              alt="Menu Icon"
            />
          </button>
          <a className="logo-link" href="/home.html">
            <img
              className="logo-icon"
              src={require("../images/Icons/Logo/logo-icon.png")}
              alt="Logo Icon"
            />
          </a>
          <ul className="menu-session">
            <li className="menu-session__icon">
              <a className="menu-profile">
                <img
                  className="profile-icon"
                  src={require("../images/Icons/profile-icon.png")}
                  alt="Profile Icon"
                />
              </a>
            </li>
            <li className="menu-session__icon">
              <a className="menu-shopping-bag">
                <img
                  className="shopping-bag-icon"
                  src={require("../images/Icons/shopping-bag-icon.png")}
                  alt="Shopping Icon"
                />
              </a>
            </li>
          </ul>
        </nav>
      </header>
    </>
  );
};
