import { Footer } from "../../components/footer";
import { Header } from "../../components/header";
import { Navigation } from "../../components/navigation";
import { Product } from "../../components/product/product";
import { Filter } from "../../components/filter";

import "./products.css";
import "../../css/general.css";

import axios from "axios";
import { useEffect, useState } from "react";

export const Products = () => {
  const [products, setProducts] = useState();
  // Fetch API to get data from mysql
  useEffect(() => {
    const retrieveProducts = async () => {
      const retrieveProductsAPI = await axios
        .get("http://localhost:5000/products")
        .then((d) => d.data);
      setProducts(retrieveProductsAPI);
    };
    retrieveProducts();
  }, []);

  return (
    <>
      <Navigation />
      <Header />
      <main class="main-content">
        <section class="deals-banner">
          <a>
            <h1 class="deals-title">Newly added gear from $9.99</h1>
            <p class="deals-description">Sale going from 9/12 - 10/2</p>
          </a>
        </section>
        <section class="filter-content">
          <Filter
            title="Brand"
            options={[
              "Sony",
              "Canon",
              "FUJIFILM",
              "HasselBlad",
              "Kodak",
              "Polaroid",
            ]}
          />
          <Filter
            title="Sensor Size"
            options={[
              "Full Frame (35mm)",
              "Medium Format",
              '1"',
              '1/2.3"',
              '1.5"',
              '1.2"',
              "APS-C",
            ]}
          />
          <Filter
            title="Image Resolution"
            options={[
              "102 Megapixels",
              "100 Megapixels",
              "64 Megapixels",
              "61 Megapixels",
              "60 Megapixels",
              "50 Megapixels",
            ]}
          />
          <h1 class="filter-title">Price</h1>
          <p class="filter-description">Enter a price range:</p>
          <section class="price-textbox">
            <input
              type="text"
              class="min-price"
              name="min-price"
              placeholder="$"
            />
            <input
              type="text"
              class="max-price"
              name="max-price"
              placeholder="$"
            />
          </section>
          <button class="price-filter-btn">Go</button>
        </section>
        <section class="filter-section">
          <Filter
            title="Brand"
            options={[
              "Sony",
              "Canon",
              "FUJIFILM",
              "HasselBlad",
              "Kodak",
              "Polaroid",
            ]}
          />
          <Filter
            title="Sensor Size"
            options={[
              "Full Frame (35mm)",
              "Medium Format",
              '1"',
              '1/2.3"',
              '1.5"',
              '1.2"',
              "APS-C",
            ]}
          />
          <Filter
            title="Image Resolution"
            options={[
              "102 Megapixels",
              "100 Megapixels",
              "64 Megapixels",
              "61 Megapixels",
              "60 Megapixels",
              "50 Megapixels",
            ]}
          />
        </section>
        <section>
          {products?.map((product) => (
            <Product productData={product} />
          ))}
        </section>
      </main>
      <Footer />
    </>
  );
};
