/* eslint-disable jsx-a11y/anchor-is-valid */
export const Header = () => {
  document.querySelector(".nav-btn").addEventListener("click", function () {
    const menu = document.querySelector(".navigation-slider");
    const navIcon = document.querySelector(".nav-btn");
    const invis = document.querySelector(".invisible");
    const marginProp = window
      .getComputedStyle(menu)
      .getPropertyValue("margin-left");

    if (marginProp === "-300px") {
      menu.style.marginLeft = "0px";
      navIcon.style.position = "fixed";
      invis.style.position = "relative";
    } else if (marginProp === "0px") {
      menu.style.marginLeft = "-300px";
      navIcon.style.removeProperty("position");
      invis.style.position = "absolute";
    }
  });

  document.querySelector("main").addEventListener("click", function () {
    const menu = document.querySelector(".navigation-slider");
    const navIcon = document.querySelector(".nav-btn");
    const invis = document.querySelector(".invisible");
    const marginProp = window
      .getComputedStyle(menu)
      .getPropertyValue("margin-left");

    if (marginProp === "0px") {
      menu.style.marginLeft = "-300px";
      navIcon.style.removeProperty("position");
      invis.style.position = "absolute";
    }
  });

  return (
    <>
      <header className="header-global">
        <nav className="menu">
          <section className="invisible"></section>
          <button className="nav-btn">
            <img
              className="nav-icon"
              src={require("../images/Icons/menu-icon.png")}
              alt="Menu Icon"
            />
          </button>
          <a className="logo-link" href="/home.html">
            <img
              className="logo-icon"
              src={require("../images/Icons/Logo/logo-icon.png")}
              alt="Logo Icon"
            />
          </a>
          <ul className="menu-session">
            <li className="menu-session__icon">
              <a className="menu-profile">
                <img
                  className="profile-icon"
                  src={require("../images/Icons/profile-icon.png")}
                  alt="Profile Icon"
                />
              </a>
            </li>
            <li className="menu-session__icon">
              <a className="menu-shopping-bag">
                <img
                  className="shopping-bag-icon"
                  src={require("../images/Icons/shopping-bag-icon.png")}
                  alt="Shopping Icon"
                />
              </a>
            </li>
          </ul>
        </nav>
      </header>
    </>
  );
};
